INSERT FLAVORTEXT VALUES ('dp5-47', 'It lives in forests and fields. Squabbles over territory occur when flocks collide.')
INSERT FLAVORTEXT VALUES ('dp5-48', 'It stands along paths pretending to be a tree. If it starts raining, it seems to disappear.')
