INSERT FLAVORTEXT VALUES ('hgss4-15', 'Ancient people believed that petitioning Bronzong for rain was the way to make crops grow.')
INSERT FLAVORTEXT VALUES ('hgss4-16', 'It walks around on its tentacles in search of a tree branch where it can dangle down and ambush prey.')
