INSERT FLAVORTEXT VALUES ('xy11-70', 'Any frontal attack is repulsed. It is a docile Pokémon that feeds on grass and berries.')
INSERT FLAVORTEXT VALUES ('xy11-71', 'The two minigears that mesh together are predetermined. Each will rebound from other minigears without meshing.')
