INSERT HABILIDAD VALUES ('pgo-53', 'Sudden Transformation', 'Ability', 'This Pokémon can use the attacks of any Basic Pokémon in your discard pile, except for Pokémon with a Rule Box (Pokémon V, Pokémon-GX, etc. have Rule Boxes). (You still need the necessary Energy to use each attack.)')
